var ft;
var callback;
var fileTransfer = {
    startUpload: function(fileUrl, fun){
        var uploadServer = 'http://localhost:3001/upload';

        //Upload progress
        var text = '<div id="progress" class="progress"><span class="progress-bar"></span></div>';
        Jellyfish.modal({
            title: i18n.camera.image_uploading + ' <span class="percent"></span>',
            text: text,
            buttons: [{
                text: i18n.global.cancel,
                onClick: fileTransfer.abortUpload
            }]
        });

        callback = fun;

        /* global FileUploadOptions */
        var options = new FileUploadOptions();
        options.fileKey = 'upfile';
        options.fileName = fileUrl.substr(fileUrl.lastIndexOf('/') + 1);
        options.mimeType = 'image/jpeg';
        options.params = {};
        ft = new FileTransfer();
        ft.upload(fileUrl, encodeURI(uploadServer), fileTransfer.uploadSuccess , fileTransfer.uploadFail , options);

        ft.onprogress = fileTransfer.onprogress;
    },

    uploadSuccess: function (r) {
        Jellyfish.closeModal('.modal');

        navigator.camera.cleanup();

        // var response = r.response ? JSON.parse(r.response) : '';
        // var response = JSON.parse(r);
        // var response = r;
        // var res_str = '';
        // for (var key in response) {
        //     res_str += key+ ': ' + response[key] + '|';
        // }
        // $$('#messageText').val(res_str);
        // if (typeof(callback) === 'function') {
            callback(r.response.data);
        // }
        // if (response.err_code === 0) {
        //     Jellyfish.alert(response.data);
        // }
        // else {
        //     Jellyfish.alert(response.err_msg);
        // }

    },

    uploadFail: function (error) {
        Jellyfish.closeModal('.modal');

        /* global FileTransferError */
        var errText;
        switch (error.code){
            case FileTransferError.FILE_NOT_FOUND_ERR:
                errText = i18n.camera.file_not_found_err;
                break;
            case FileTransferError.INVALID_URL_ERR:
                errText = i18n.camera.invalid_url_err;
                break;
            case FileTransferError.CONNECTION_ERR:
                errText = i18n.camera.connection_err;
                break;
            case FileTransferError.ABORT_ERR:
                errText = i18n.camera.abort_err;
                break;
            case FileTransferError.NOT_MODIFIED_ERR:
                errText = i18n.camera.not_modified_err;
                break;
        }

        Jellyfish.alert(errText);
    },

    onprogress: function(progressEvent){
        if (progressEvent.lengthComputable) {
            var percent = Math.round((progressEvent.loaded / progressEvent.total) * 100);
            $$('#progress').parents('.modal-inner').find('.modal-title .percent').html(percent + '%');
            $$('#progress').find('.progress-bar').css('width',percent + '%');
        }
    },

    abortUpload: function(){
        ft.abort();
    }
};

module.exports = fileTransfer;

var GlobalLat = null,
    GlobalLong = null;

var geolocation = {
    initGeo: function(){
        $$('#geoInfo').removeClass('show').hide();

        $$('#geoInfo .location>i').removeClass('ios7-location-outline').addClass('preloader');
        $$('#geoInfo .location>span').html(i18n.geo.loading_geo);

        GlobalLat = null;
        GlobalLong = null;
    },

    catchGeoInfo: function(){
        $$('#geoInfo').addClass('show').show();
        if (navigator.geolocation){
            navigator.geolocation.getCurrentPosition(geolocation.showPosition,geolocation.showGeoError);
        }else{
            $$('#geoInfo .location').html(i18n.geo.position_unavailable);
        }
    },

    showPosition: function(position){
        var lat = position.coords.latitude;
        var long = position.coords.longitude;

        $$('#geoInfo .location>i').removeClass('preloader').addClass('ios7-location-outline');
        $$('#geoInfo .location>span').html(( Math.round(lat * 10000)/10000) + '/' + ( Math.round(long * 10000)/10000) );

        GlobalLat = lat;
        GlobalLong = long;
    },

    showGeoError: function(error){
        switch(error.code)
        {
            case error.PERMISSION_DENIED:
                $$('#geoInfo .location').html(i18n.geo.permission_denied);
                break;
            case error.POSITION_UNAVAILABLE:
                $$('#geoInfo .location').html(i18n.geo.position_unavailable);
                break;
            case error.TIMEOUT:
                $$('#geoInfo .location').html(i18n.geo.timeout);
                break;
            case error.UNKNOWN_ERROR:
                $$('#geoInfo .location').html(i18n.error.unknown_error);
                break;
        }
    },

    getGeo: function(){
        return {
            lat:GlobalLat,
            long:GlobalLong
        };
    },

    cleanGeo: function(){
        Jellyfish.confirm(i18n.geo.confirm_clean_geo,geolocation.initGeo);
    }
};

