"@import "mixins";

.navbar{
  i{
    &.icon {
      font-size: 25px;
    }

    &.ios7-compose-outline {
      font-size: 30px;
    }

    &.ios7-arrow-thin-left {
      font-size: 32px;
    }

  }

  a{
    &.link {
      color: #333;
      &.color-orange{
        color: @mainColor;
      }
    }
  }

  a,
  div{
    &.link:active{
      opacity: 1;
      color: @mainColor
    }
  }

  .text-navbar {
    font-size: 15px;
  }
}"

".toolbar{
  i{
    &.icon {
      font-size: 28px;
      line-height: 28px;
    }
  }

  .active{
    .ios7-home-outline{
      &:before {
        content: "\e619";
      }
    }

    .ios7-chatbubble-outline{
      &:before {
        content: "\e61b";
      }
    }

    .ios7-gear-outline{
      &:before {
        content: "\e60c";
      }
    }
  }

  a{
    &.active {
      color: @mainColor;
    }
  }

  span{
    &.tabbar-label {
      font-size: 12px;
    }
  }
}"

